public class Tiptup {
    private static final int MARKER_19A_TIPTUP = 0x19A;
    private static final int ACTOR_27A_TIPTUP = 0x27A;
    private static final int ASSET_3F8_MODEL_TIPTUP = 0x3F8;

    private static final float[] JIGGY_POSITIONS = {0.0f, 100.0f, -1020.0f};
    private static final int[] SEQUENCE_LENGTHS = {3, 3, 5, 7};

    private boolean initialized;
    private ArrayList<Integer> choirSequence;
    private int currentState;
    private int progressCount;
    private float timer;
    private Vector3 position;
    private Model model;
    private Animation currentAnim;

    public Tiptup() {
        choirSequence = new ArrayList<>();
        position = new Vector3();
        model = ModelLoader.load(ASSET_3F8_MODEL_TIPTUP);
        loadAnimations();
    }

    public void init() {
        if (!initialized) {
            setState(1);
            progressCount = GameProgress.getTiptupProgress();
            if (GameFlags.isMinigameActive()) {
                position.y -= 300.0f;
                progressCount = 3;
            }
            initialized = true;
        }
    }

    public void update(float deltaTime) {
        if (!initialized) return;

        updateAnimation(deltaTime);
        updateState(deltaTime);
        updateChoir();

        timer -= deltaTime;
    }

    public void setState(int newState) {
        currentState = newState;
        timer = 0;

        switch(newState) {
            case 1:
                playIdleAnimation();
                timer = Random.nextFloat() * 10 + 5;
                break;
            case 3:
                generateNewSequence();
                break;
            case 6:
                if (progressCount == 3) {
                    AudioManager.playJingle(SOUND_PUZZLE_SOLVED);
                    spawnJiggy();
                }
                break;
        }
    }

    private void generateNewSequence() {
        choirSequence.clear();
        int length = SEQUENCE_LENGTHS[progressCount];
        int prevNote = 0;

        for (int i = 0; i < length; i++) {
            int note;
            do {
                note = Random.nextInt(7) + 1;
            } while (note == prevNote);
            choirSequence.add(note);
            prevNote = note;
        }
    }

    public void handleChoirHit(int noteHit) {
        if (noteHit == choirSequence.get(currentSequencePos)) {
            AudioManager.playSound(SOUND_CORRECT);
            currentSequencePos++;
            if (currentSequencePos >= choirSequence.size()) {
                onSequenceComplete();
            }
        } else {
            AudioManager.playSound(SOUND_WRONG);
            setState(4);
        }
    }

    private void onSequenceComplete() {
        progressCount++;
        GameProgress.saveTiptupProgress(progressCount);
        if (progressCount >= 3) {
            setState(6);
        } else {
            setState(3);
        }
    }

    public void render() {
        if (!initialized) return;

        shader.use();
        model.bind();
        currentAnim.apply();
        model.draw();
    }

    public void dispose() {
        model.dispose();
        choirSequence.clear();
    }
}
