        model = ModelLoader.load(ASSET_425_MODEL_CROCTUS);
        shader = new Shader("croctus.vert", "croctus.frag");
        loadAnimations();
    }

    public void init() {
        if (initialized) return;

        if (JiggyManager.isCollected(JIGGY_22_BGS_CROCTUS)) {
            dispose();
            return;
        }

        currentState = 1;
        hitCount = 0;
        stateTimer = 0;
        hasSpawnedJiggy = false;
        initialized = true;
    }

    public void update(float deltaTime) {
        if (!initialized) return;

        stateTimer += deltaTime;
        updatePatterns(deltaTime);
        updateState();
        updateAnimation(deltaTime);

        Vector3 playerPos = PlayerManager.getPosition();
        float targetYaw = calculateYawToTarget(playerPos);
        yaw = lerpAngle(yaw, targetYaw, deltaTime * 5.0f);
    }

    private void updatePatterns(float deltaTime) {
        for (CroctusPattern pattern : patterns) {
            pattern.timer -= deltaTime;
            if (pattern.timer <= 0) {
                handlePatternComplete(pattern);
            }
        }
    }

    private void updateState() {
        switch (currentState) {
            case 1: // Idle
                handleIdleState();
                break;
            case 2: // Pattern Display
                handlePatternState();
                break;
            case 3: // Player Input
                handleInputState();
                break;
            case 4: // Success
                handleSuccessState();
                break;
            case 5: // Failure
                handleFailureState();
                break;
        }
    }

    public void handleHit(int hitValue) {
        if (currentState != 3) return;

        if (hitValue == patterns.get(hitCount).pattern) {
            AudioManager.playSound(SOUND_CORRECT);
            hitCount++;

            if (hitCount >= patterns.size()) {
                currentState = 4;
                spawnJiggy();
            }
        } else {
            AudioManager.playSound(SOUND_WRONG);
            currentState = 5;
            resetPatterns();
        }
    }

    private void spawnJiggy() {
        if (hasSpawnedJiggy) return;

        JiggyManager.spawn(JIGGY_22_BGS_CROCTUS, JIGGY_SPAWN_POS);
        SteamEffect.spawn(JIGGY_SPAWN_POS);
        hasSpawnedJiggy = true;
    }

    private void generateNewPattern() {
        patterns.clear();
        int length = SEQUENCE_LENGTHS[Math.min(hitCount / 3, 3)];

        for (int i = 0; i < length; i++) {
            CroctusPattern pattern = new CroctusPattern();
            pattern.pattern = Random.nextInt(7) + 1;
            pattern.duration = 1.0f;
            pattern.timer = pattern.duration;
            patterns.add(pattern);
        }
    }

    public void render() {
        if (!initialized) return;

        shader.use();
        Matrix.setIdentityM(modelMatrix, 0);
        Matrix.translateM(modelMatrix, 0, position.x, position.y, position.z);
        Matrix.rotateM(modelMatrix, 0, yaw, 0, 1, 0);

        shader.setMatrix4("uModel", modelMatrix);
        model.bind();
        currentAnim.apply();
        model.draw();
    }

    public void dispose() {
        if (model != null) model.dispose();
        if (shader != null) shader.dispose();
        patterns.clear();
        initialized = false;
    }
}
