        if (!initialized) return;

        updateAnimation(deltaTime);
        updateMouth(deltaTime);
        updateEggTracking();
        updateState(deltaTime);
        updateEffects(deltaTime);

        if (local.isAwake) {
            Vector3 playerPos = PlayerManager.getPosition();
            float targetYaw = calculateYawToTarget(playerPos);
            yaw = lerpAngle(yaw, targetYaw, deltaTime * 2.0f);
        }
    }

    private void updateMouth(float deltaTime) {
        if (local.mouthOpenAmount != local.targetMouthOpen) {
            float dir = local.targetMouthOpen > local.mouthOpenAmount ? 1.0f : -1.0f;
            local.mouthOpenAmount += dir * deltaTime * 2.0f;

            if (Math.abs(local.mouthOpenAmount - local.targetMouthOpen) < 0.01f) {
                local.mouthOpenAmount = local.targetMouthOpen;
            }
        }
    }

    private void updateEggTracking() {
        for (PinkEgg egg : local.trackedEggs) {
            if (Vector3.distance(position, egg.getPosition()) < 100.0f) {
                eatEgg(egg);
            }
        }
    }

    private void eatEgg(PinkEgg egg) {
        local.eggsEaten++;
        local.trackedEggs.remove(egg);
        egg.dispose();

        AudioManager.playSound(SOUND_EGG_EAT);
        bubbleEffect.burstAt(egg.getPosition());

        if (local.eggsEaten >= 5) {
            wakeUp();
        }
    }

    private void wakeUp() {
        if (!local.isAwake) {
            local.isAwake = true;
            playAnimation(ANIM_TANKTUP_WAKE);
            voiceSource.play(SOUND_TANKTUP_WAKE);
            spawnJiggy();
        }
