        position = new Vector3();
        local = new FlibbitLocal();
        model = ModelLoader.load(ASSET_378_MODEL_YELLOW_FLIBBIT);
        physics = new PhysicsBody(PhysicsType.CHARACTER);
        noteEffect = new ParticleSystem(EFFECT_MUSICAL_NOTE);
