        loadAnimations();
    }

    public void init() {
        if (initialized) return;

        marker.setCollisionScripts(this::onCollision, null, null);
        local.startPos.set(position);
        local.noteIndex = 0;
        local.isActive = false;
        setState(STATE_IDLE);

        initialized = true;
    }

    public void update(float deltaTime) {
        if (!initialized) return;

        updateAnimation(deltaTime);
        updateState(deltaTime);
        updateJumping(deltaTime);
        updateNotes(deltaTime);
        updateEffects(deltaTime);

        if (local.isActive) {
            Vector3 playerPos = PlayerManager.getPosition();
            float targetYaw = calculateYawToTarget(playerPos);
            yaw = lerpAngle(yaw, targetYaw, deltaTime * 3.0f);
        }
    }

    private void updateState(float deltaTime) {
        stateTimer += deltaTime;

        switch (currentState) {
            case STATE_IDLE:
                handleIdleState();
                break;
            case STATE_PREPARE_JUMP:
                handlePrepareJumpState();
                break;
            case STATE_JUMPING:
                handleJumpingState(deltaTime);
                break;
            case STATE_LANDING:
                handleLandingState();
                break;
            case STATE_SINGING:
                handleSingingState(deltaTime);
                break;
        }
    }

    private void handleJumpingState(float deltaTime) {
        if (local.isJumping) {
            local.jumpProgress += deltaTime * 2.0f;
            float jumpCurve = (float)Math.sin(local.jumpProgress * Math.PI);

            position.y = local.startPos.y + local.jumpHeight * jumpCurve;

            if (local.jumpProgress >= 1.0f) {
                land();
            }
        }
    }

    private void handleSingingState(float deltaTime) {
        local.noteTimer -= deltaTime;

        if (local.noteTimer <= 0) {
            playNote();
            local.noteTimer = 0.5f;
        }
    }

    private void playNote() {
        int note = NOTE_SEQUENCE[local.noteIndex];
        AudioManager.playSound(SOUND_NOTE_BASE + note);
        spawnNoteEffect(note);

        local.noteIndex = (local.noteIndex + 1) % NOTE_SEQUENCE.length;
        FrogMinigame.onNoteHit(note);
    }

