package com.banjo.game.levels.bgs.code;

public class BgsCrc {
    private static final long CRC_INITIAL = 0x0A2D7D03L;
    private static final long CRC_POLYNOMIAL = 0xEDB88320L;
    private static final int CRC_TABLE_SIZE = 256;

    private static long[] crcTable = new long[CRC_TABLE_SIZE];

    static {
        initializeCrcTable();
    }

    private static void initializeCrcTable() {
        for (int i = 0; i < CRC_TABLE_SIZE; i++) {
            long crc = i;
            for (int j = 0; j < 8; j++) {
                if ((crc & 1) == 1) {
                    crc = (crc >>> 1) ^ CRC_POLYNOMIAL;
                } else {
                    crc = crc >>> 1;
                }
            }
            crcTable[i] = crc;
        }
    }

    public static long calculateCrc(byte[] data) {
        long crc = CRC_INITIAL;
        for (byte b : data) {
            crc = (crc >>> 8) ^ crcTable[(int)((crc & 0xFF) ^ b)];
        }
        return crc;
    }
}
