public class Sawblade {
    private static final int MARKER_CC_SAWBLADE = 0x1A0;
    private static final int ACTOR_CC_SAWBLADE = 0x280;
    private static final int ASSET_MODEL_SAWBLADE = 0x400;
    private static final float ROTATION_SPEED = 720.0f;
    private static final float DAMAGE_AMOUNT = 2.0f;

    private class SawbladeState {
        Vector3 startPos = new Vector3();
        Vector3 currentPos = new Vector3();
        float rotationAngle;
        float moveTimer;
        boolean isMoving;
        Path movementPath;
    }

    private SawbladeState state;
    private Model model;
    private PhysicsBody physics;
    private ParticleSystem sparkEffect;
    private AudioSource soundSource;
    private boolean initialized;

    public Sawblade() {
        state = new SawbladeState();
        model = ModelLoader.load(ASSET_MODEL_SAWBLADE);
        physics = new PhysicsBody(PhysicsType.KINEMATIC);
        sparkEffect = new ParticleSystem(EFFECT_SPARKS);
        soundSource = new AudioSource(3D_ENABLED);
        setupCollision();
    }

    private void setupCollision() {
        physics.setCollisionShape(CollisionShape.CYLINDER);
        physics.setCollisionRadius(32.0f);
        physics.setCollisionHeight(8.0f);
        physics.setCollisionCallback(this::onCollision);
    }

    public void initialize(Vector3 position, Path path) {
        if (initialized) return;

        state.startPos.set(position);
        state.currentPos.set(position);
        state.movementPath = path;
        state.isMoving = true;
        state.moveTimer = 0.0f;
        state.rotationAngle = 0.0f;

        soundSource.play(SOUND_SAWBLADE_SPIN, true);
        initialized = true;
    }

    public void update(float deltaTime) {
        if (!initialized) return;

        updateRotation(deltaTime);
        updateMovement(deltaTime);
        updateEffects(deltaTime);
        updatePhysics();
    }

    private void updateRotation(float deltaTime) {
        state.rotationAngle += ROTATION_SPEED * deltaTime;
        if (state.rotationAngle >= 360.0f) {
            state.rotationAngle -= 360.0f;
        }
    }

    private void updateMovement(float deltaTime) {
        if (state.isMoving && state.movementPath != null) {
            state.moveTimer += deltaTime;
            Vector3 newPos = state.movementPath.getPosition(state.moveTimer);
            state.currentPos.set(newPos);
        }
    }

    private void updateEffects(float deltaTime) {
        sparkEffect.setPosition(state.currentPos);
        sparkEffect.update(deltaTime);

        float volume = state.isMoving ? 1.0f : 0.5f;
        soundSource.setVolume(volume);
    }

    private void updatePhysics() {
        physics.setPosition(state.currentPos);
        physics.setRotationY(state.rotationAngle);
    }

    private void onCollision(Actor other) {
        if (other instanceof Player) {
            damagePlayer((Player)other);
            createSparkEffect();
        }
    }

    private void damagePlayer(Player player) {
        player.damage(DAMAGE_AMOUNT);
        player.applyKnockback(state.currentPos);
    }

    private void createSparkEffect() {
        sparkEffect.burst(20);
        soundSource.playOneShot(SOUND_SAWBLADE_HIT);
    }

    public void render() {
        if (!initialized) return;

        Shader.use(SHADER_ACTOR);
        Matrix4f modelMatrix = new Matrix4f()
            .translate(state.currentPos)
            .rotateY(state.rotationAngle);

        Shader.setMatrix4("uModel", modelMatrix);
        model.draw();
        sparkEffect.render();
    }

    public void dispose() {
        model.dispose();
        physics.dispose();
        sparkEffect.dispose();
        soundSource.dispose();
    }
}
