package com.banjo.game.levels.cc.actors;

import com.banjo.game.core.*;
import com.banjo.game.physics.*;
import com.banjo.game.effects.*;

public class Tooth {
    private static final int MARKER_CC_TOOTH = 0x1A1;
    private static final int ACTOR_CC_TOOTH = 0x281;
    private static final int ASSET_MODEL_TOOTH = 0x401;
    private static final float SHAKE_DURATION = 1.5f;
    private static final float FALL_SPEED = 300.0f;

    private class ToothState {
        Vector3 startPos = new Vector3();
        Vector3 currentPos = new Vector3();
        float shakeTimer;
        float fallDistance;
        boolean isShaking;
        boolean isFalling;
    }

    private ToothState state;
    private Model model;
    private PhysicsBody physics;
    private ParticleSystem dustEffect;
    private AudioSource soundSource;
    private boolean initialized;

    public Tooth() {
        state = new ToothState();
        model = ModelLoader.load(ASSET_MODEL_TOOTH);
        physics = new PhysicsBody(PhysicsType.KINEMATIC);
        dustEffect = new ParticleSystem(EFFECT_DUST);
        soundSource = new AudioSource(3D_ENABLED);
        setupCollision();
    }

    private void setupCollision() {
        physics.setCollisionShape(CollisionShape.BOX);
        physics.setCollisionSize(new Vector3(32.0f, 64.0f, 32.0f));
        physics.setCollisionCallback(this::onCollision);
    }

    public void initialize(Vector3 position) {
        if (initialized) return;

        state.startPos.set(position);
        state.currentPos.set(position);
        state.shakeTimer = 0.0f;
        state.fallDistance = 0.0f;
        state.isShaking = false;
        state.isFalling = false;

        initialized = true;
    }

    public void update(float deltaTime) {
        if (!initialized) return;

        updateShake(deltaTime);
        updateFall(deltaTime);
        updateEffects(deltaTime);
        updatePhysics();
    }

    private void updateShake(float deltaTime) {
        if (state.isShaking) {
            state.shakeTimer += deltaTime;
            float offset = (float)Math.sin(state.shakeTimer * 20.0f) * 2.0f;
            state.currentPos.x = state.startPos.x + offset;

            if (state.shakeTimer >= SHAKE_DURATION) {
                startFalling();
            }
        }
    }

    private void updateFall(float deltaTime) {
        if (state.isFalling) {
            state.fallDistance += FALL_SPEED * deltaTime;
            state.currentPos.y = state.startPos.y - state.fallDistance;

            if (state.currentPos.y <= 0) {
                onLanding();
            }
        }
    }

    private void startFalling() {
        state.isShaking = false;
        state.isFalling = true;
        soundSource.play(SOUND_TOOTH_FALL);
    }

    private void onLanding() {
        state.isFalling = false;
        dustEffect.burst(30);
        soundSource.play(SOUND_TOOTH_LAND);
        physics.setEnabled(false);
    }

    public void render() {
        if (!initialized) return;

        Shader.use(SHADER_ACTOR);
        Matrix4f modelMatrix = new Matrix4f().translate(state.currentPos);
        Shader.setMatrix4("uModel", modelMatrix);
        model.draw();
        dustEffect.render();
    }

    public void dispose() {
        model.dispose();
        physics.dispose();
        dustEffect.dispose();
        soundSource.dispose();
    }
}
