package com.banjo.game.levels.cc.code;

import com.banjo.game.core.*;
import com.banjo.game.actors.*;
import com.banjo.game.effects.*;
import com.banjo.game.cc.actors.*;

public class CCGameplay {
    private static final int CC_GAMEPLAY_CODE = 0x13C0;
    private static final float WATER_DRAIN_RATE = 0.5f;
    private static final float MAX_WATER_LEVEL = 100.0f;
    private static final float MIN_WATER_LEVEL = -100.0f;

    private GameState state;
    private WaterSystem water;
    private EventSystem events;
    private CameraSystem camera;
    private ArrayList<Trigger> triggers;
    private HashMap<Integer, Cutscene> cutscenes;

    public CCGameplay() {
        state = new GameState();
        water = new WaterSystem();
        events = new EventSystem();
        camera = new CameraSystem();
        triggers = new ArrayList<>();
        cutscenes = new HashMap<>();
        initializeSystems();
    }

    private void initializeSystems() {
        setupWaterSystem();
        setupEventTriggers();
        setupCutscenes();
        setupCameraPoints();
    }

    private void setupWaterSystem() {
        water.setLevel(MAX_WATER_LEVEL);
        water.setDrainRate(WATER_DRAIN_RATE);
        water.setPhysicsProperties(1.0f, 0.3f);
        water.enableRipples(true);
    }

    private void setupEventTriggers() {
        triggers.add(new Trigger(TRIGGER_CLANKER_WAKE, new Vector3(-100, 0, -200)));
        triggers.add(new Trigger(TRIGGER_WATER_DRAIN, new Vector3(200, 0, 100)));
        triggers.add(new Trigger(TRIGGER_GOLD_FEATHER, new Vector3(0, 100, 300)));
    }

    private void setupCutscenes() {
        cutscenes.put(CUTSCENE_CLANKER_INTRO, new ClankerIntroCutscene());
        cutscenes.put(CUTSCENE_WATER_DRAIN, new WaterDrainCutscene());
        cutscenes.put(CUTSCENE_JIGGY_GET, new JiggyGetCutscene());
    }

    public void update(float deltaTime) {
        updateWaterLevel(deltaTime);
        checkTriggers();
        updateEvents(deltaTime);
        updateCamera(deltaTime);
        updateCutscenes(deltaTime);
    }

    private void updateWaterLevel(float deltaTime) {
        if (state.isWaterDraining()) {
            float newLevel = water.getLevel() - (WATER_DRAIN_RATE * deltaTime);
            water.setLevel(Math.max(newLevel, MIN_WATER_LEVEL));
            updateWaterEffects();
        }
    }

    private void updateWaterEffects() {
        water.updateSurface();
        water.updateReflections();
        water.updateParticles();
        water.updateAudio();
    }

    private void checkTriggers() {
        Vector3 playerPos = state.getPlayerPosition();
        for (Trigger trigger : triggers) {
            if (trigger.checkCollision(playerPos)) {
                handleTrigger(trigger.getId());
            }
        }
    }

    private void handleTrigger(int triggerId) {
        switch (triggerId) {
            case TRIGGER_CLANKER_WAKE:
                startClankerEvent();
                break;
            case TRIGGER_WATER_DRAIN:
                startWaterDrain();
                break;
            case TRIGGER_GOLD_FEATHER:
                giveGoldFeather();
                break;
        }
    }

    private void startClankerEvent() {
        events.trigger(EVENT_CLANKER_WAKE);
        camera.setCinematicMode(true);
        cutscenes.get(CUTSCENE_CLANKER_INTRO).start();
    }

    private void startWaterDrain() {
        state.setWaterDraining(true);
        events.trigger(EVENT_WATER_DRAIN);
        playDrainAnimation();
    }

    private void updateCamera(float deltaTime) {
        if (camera.isCinematicMode()) {
            camera.updateCinematic(deltaTime);
        } else {
            camera.updateGameplay(deltaTime);
        }
    }

    private void updateCutscenes(float deltaTime) {
        for (Cutscene cutscene : cutscenes.values()) {
            if (cutscene.isPlaying()) {
                cutscene.update(deltaTime);
            }
        }
    }

    public void render() {
        water.render();
        for (Cutscene cutscene : cutscenes.values()) {
            if (cutscene.isPlaying()) {
                cutscene.render();
            }
        }
    }
}
