package com.banjo.game.levels.cc.code;

import com.banjo.game.core.*;
import com.banjo.game.actors.*;
import com.banjo.game.effects.*;
import com.banjo.game.physics.*;
import com.banjo.game.animation.*;
import com.banjo.game.audio.*;
import com.banjo.game.cc.actors.*;

public class CCClanker {
    private static final int CC_CLANKER_CODE = 0x1B90;
    private static final float ROTATION_SPEED = 45.0f;
    private static final float CHAIN_LENGTH = 200.0f;
    private static final int NUM_TEETH = 8;

    private class ClankerState {
        Vector3 position = new Vector3(0, -100, 0);
        Vector3 rotation = new Vector3();
        float currentRotation = 0.0f;
        float targetRotation = 0.0f;
        boolean isAwake = false;
        boolean isRotating = false;
    }

    private ClankerState state;
    private Model clankerModel;
    private AnimationController animator;
    private PhysicsBody physics;
    private ParticleSystem bubbles;
    private AudioSource audio;
    private ArrayList<Chain> chains;
    private ArrayList<Tooth> teeth;

    public CCClanker() {
        state = new ClankerState();
        initializeSystems();
        setupPhysics();
        createChains();
        createTeeth();
    }

    private void initializeSystems() {
        clankerModel = ModelLoader.load(MODEL_CLANKER);
        animator = new AnimationController(ANIM_SET_CLANKER);
        physics = new PhysicsBody(PhysicsType.KINEMATIC);
        bubbles = new ParticleSystem(EFFECT_BUBBLES);
        audio = new AudioSource(3D_ENABLED);
        chains = new ArrayList<>();
        teeth = new ArrayList<>();
    }

    private void setupPhysics() {
        physics.setCollisionShape(CollisionShape.BOX);
        physics.setCollisionSize(new Vector3(300, 200, 500));
        physics.setMass(10000.0f);
        physics.setRestitution(0.3f);
    }

    private void createChains() {
        for(int i = 0; i < 4; i++) {
            Chain chain = new Chain(CHAIN_LENGTH);
            chain.setAnchorPoint(calculateChainAnchor(i));
            chains.add(chain);
        }
    }

    private void createTeeth() {
        for(int i = 0; i < NUM_TEETH; i++) {
            Tooth tooth = new Tooth();
            tooth.initialize(calculateToothPosition(i));
            teeth.add(tooth);
        }
    }

    public void update(float deltaTime) {
        updateRotation(deltaTime);
        updateAnimation(deltaTime);
        updatePhysics();
        updateEffects(deltaTime);
        updateChains(deltaTime);
        updateTeeth(deltaTime);
    }

    private void updateRotation(float deltaTime) {
        if (state.isRotating) {
            state.currentRotation = Mathf.lerp(
                state.currentRotation,
                state.targetRotation,
                deltaTime * ROTATION_SPEED
            );
        }
    }

    private void updateAnimation(float deltaTime) {
        if (state.isAwake) {
            animator.update(deltaTime);
        }
    }

    private void updateEffects(float deltaTime) {
        bubbles.setPosition(state.position);
        bubbles.update(deltaTime);
        updateAudio();
    }

    public void render() {
        Shader.use(SHADER_ACTOR);
        Matrix4f modelMatrix = new Matrix4f()
            .translate(state.position)
            .rotate(state.rotation);

        Shader.setMatrix4("uModel", modelMatrix);
        clankerModel.draw();

        renderChains();
        renderTeeth();
        bubbles.render();
    }

    private void renderChains() {
        for(Chain chain : chains) {
            chain.render();
        }
    }

    private void renderTeeth() {
        for(Tooth tooth : teeth) {
            tooth.render();
        }
    }

    public void dispose() {
        clankerModel.dispose();
        physics.dispose();
        bubbles.dispose();
        audio.dispose();

        for(Chain chain : chains) {
            chain.dispose();
        }

        for(Tooth tooth : teeth) {
            tooth.dispose();
        }
    }
}
