public class CCCamera {
    private static final int CC_CAMERA_CODE = 0xBF0;
    private static final float FOLLOW_SPEED = 5.0f;
    private static final float ROTATION_SPEED = 180.0f;
    private static final float MIN_DISTANCE = 100.0f;
    private static final float MAX_DISTANCE = 300.0f;

    private Vector3 position;
    private Vector3 target;
    private Vector3 up;
    private float distance;
    private float yaw;
    private float pitch;
    private boolean isLocked;
    private CameraMode currentMode;

    private enum CameraMode {
        FOLLOW,
        FIXED,
        CINEMATIC
    }

    public CCCamera() {
        position = new Vector3(0, 100, -200);
        target = new Vector3();
        up = new Vector3(0, 1, 0);
        distance = 200.0f;
        yaw = 0.0f;
        pitch = 30.0f;
        currentMode = CameraMode.FOLLOW;
    }

    public void update(float deltaTime) {
        switch(currentMode) {
            case FOLLOW:
                updateFollowCamera(deltaTime);
                break;
            case FIXED:
                updateFixedCamera(deltaTime);
                break;
            case CINEMATIC:
                updateCinematicCamera(deltaTime);
                break;
        }
        updateTransform();
    }

    private void updateFollowCamera(float deltaTime) {
        handleInput(deltaTime);
        smoothFollow(deltaTime);
        clampDistance();
        avoidObstacles();
    }

    private void handleInput(float deltaTime) {
        if(!isLocked) {
            yaw += Input.getMouseDeltaX() * ROTATION_SPEED * deltaTime;
            pitch += Input.getMouseDeltaY() * ROTATION_SPEED * deltaTime;
            pitch = Mathf.clamp(pitch, -80.0f, 80.0f);

            distance -= Input.getMouseScroll() * 50.0f;
            distance = Mathf.clamp(distance, MIN_DISTANCE, MAX_DISTANCE);
        }
    }

    private void smoothFollow(float deltaTime) {
        Vector3 targetPos = target.add(calculateOffset());
        position = Vector3.lerp(position, targetPos, FOLLOW_SPEED * deltaTime);
    }

    private Vector3 calculateOffset() {
        float horizontalDistance = distance * Mathf.cos(Mathf.radians(pitch));
        float verticalDistance = distance * Mathf.sin(Mathf.radians(pitch));

        float offsetX = horizontalDistance * Mathf.sin(Mathf.radians(yaw));
        float offsetZ = horizontalDistance * Mathf.cos(Mathf.radians(yaw));

        return new Vector3(offsetX, verticalDistance, offsetZ);
    }

    private void updateTransform() {
        Matrix4f viewMatrix = Matrix4f.createLookAt(position, target, up);
        Shader.setMatrix4("uView", viewMatrix);
    }

    public void setTarget(Vector3 newTarget) {
        target = newTarget;
    }

    public void setCinematicPath(CameraPath path) {
        currentMode = CameraMode.CINEMATIC;
        // Implementation for cinematic camera path following
    }

    public void setMode(CameraMode mode) {
        currentMode = mode;
    }

    public void setLocked(boolean locked) {
        isLocked = locked;
    }
}
