        super(position);
        spawnPoint = position.copy();
        setupComponents();
        initializeStates();
    }

    private void setupComponents() {
        animator = new AnimationController(this);
        animator.addAnimation("idle", "grublin_idle");
        animator.addAnimation("walk", "grublin_walk");
        animator.addAnimation("shoot", "grublin_shoot");
        animator.addAnimation("hit", "grublin_hit");
        animator.addAnimation("death", "grublin_death");

        projectiles = new ProjectileManager();
        pathFinder = new PathFinder();

        setupCollision();
        setupCombat();
    }

    private void setupCollision() {
        setCollisionShape(CollisionShape.CAPSULE);
        setCollisionRadius(20.0f);
        setCollisionHeight(50.0f);
    }

    private void setupCombat() {
        setMaxHealth(3);
        setCurrentHealth(3);
        setAttackDamage(ARROW_DAMAGE);
    }

    private void initializeStates() {
        stateMachine = new StateMachine<>(this);

        stateMachine.addState("patrol")
            .onEnter(this::startPatrol)
            .onUpdate(this::updatePatrol)
            .onExit(this::endPatrol);

        stateMachine.addState("chase")
            .onEnter(this::startChase)
            .onUpdate(this::updateChase)
            .onExit(this::endChase);

        stateMachine.addState("attack")
            .onEnter(this::startAttack)
            .onUpdate(this::updateAttack)
            .onExit(this::endAttack);

        stateMachine.setState("patrol");
    }

    @Override
    public void update(float deltaTime) {
        super.update(deltaTime);
        stateMachine.update(deltaTime);
        animator.update(deltaTime);
        projectiles.update(deltaTime);
        updateAttackCooldown(deltaTime);
    }

    private void updateAttackCooldown(float deltaTime) {
        if(attackCooldown > 0) {
            attackCooldown -= deltaTime;
        }
    }

