        if(attackCooldown <= 0) {
            Vector3 direction = getDirectionToPlayer();
            Projectile arrow = new Arrow(getPosition(), direction);
            projectiles.spawn(arrow);
            playSound("arrow_shoot");
            attackCooldown = 2.0f;
        }
