            if(actor instanceof Vegetation) {
                ((Vegetation)actor).updateForSeason(season);
            }
        }
    }

    private void updateWaterState(Season season) {
        WaterSystem water = state.getWater();
        water.setFrozen(season == Season.WINTER);
        water.updatePhysics();
    }

    public void update(float deltaTime) {
        seasonSystem.update(deltaTime);
        weatherSystem.update(deltaTime);
        timeSystem.update(deltaTime);
        updateSeasonalActors(deltaTime);
    }

    private void updateSeasonalActors(float deltaTime) {
        for(SeasonalActor actor : seasonalActors) {
            actor.update(deltaTime);
            actor.updateSeasonalBehavior(seasonSystem.getCurrentSeason());
        }
    }

    public void render() {
        state.getTerrain().render();
        for(SeasonalActor actor : seasonalActors) {
            actor.render();
        }
        weatherSystem.render();
    }

    public void dispose() {
        seasonSystem.dispose();
        weatherSystem.dispose();
        timeSystem.dispose();
        for(SeasonalActor actor : seasonalActors) {
            actor.dispose();
