    public void changeSeason(Season newSeason) {
        if (currentSeason != newSeason) {
            currentSeason = newSeason;
            SeasonData data = seasonData.get(newSeason);

            updateEnvironment(data);
            updateParticles(data);
            updateMusic(data);
            notifySeasonalObjects();
        }
    }

    private void updateEnvironment(SeasonData data) {
        environment.setTemperature(data.temperature);
        environment.setPrecipitation(data.precipitation);
        environment.setWindStrength(data.windStrength);
        environment.loadTexturePack(data.texturePack);
    }

    private void updateParticles(SeasonData data) {
        particles.clearEffects();
        for(ParticleEffect effect : data.effects) {
            particles.addEffect(effect);
        }
    }

    private void updateMusic(SeasonData data) {
        audio.fadeOut(1.0f);
        audio.playMusic(data.musicTrack, true);
        audio.fadeIn(1.0f);
    }

    private void notifySeasonalObjects() {
        for(SeasonalObject obj : seasonalObjects) {
            obj.onSeasonChange(currentSeason);
        }
    }

    public void update(float deltaTime) {
        environment.update(deltaTime);
        particles.update(deltaTime);
        updateSeasonalObjects(deltaTime);
    }

    private void updateSeasonalObjects(float deltaTime) {
        for(SeasonalObject obj : seasonalObjects) {
            obj.update(deltaTime);
        }
    }
