import com.banjo.game.camera.*;

public class CCWEvents {
    private static final int CCW_EVENTS_CODE = 0x1B20;

    private EventManager eventManager;
    private CutsceneManager cutscenes;
    private CameraSystem camera;
    private ParticleSystem effects;
    private AudioSystem audio;
    private ArrayList<EventTrigger> triggers;

    public CCWEvents() {
        initializeSystems();
        setupEventTriggers();
        registerEventHandlers();
    }

    private void initializeSystems() {
        eventManager = new EventManager();
        cutscenes = new CutsceneManager();
        camera = new CameraSystem();
        effects = new ParticleSystem();
        audio = new AudioSystem();
        triggers = new ArrayList<>();
    }

    private void setupEventTriggers() {
        // Season Change Events
        createSeasonChangeTrigger(new Vector3(0, 100, 200));

        // Eyrie Events
        createEyrieNestTrigger(new Vector3(50, 150, -100));

        // Zubba Hive Events
        createZubbaHiveTrigger(new Vector3(-150, 80, 100));

        // Gnawty Dam Events
        createGnawtyDamTrigger(new Vector3(200, -20, 50));
    }

    private void registerEventHandlers() {
        eventManager.register(EventType.SEASON_CHANGE, this::handleSeasonChange);
        eventManager.register(EventType.EYRIE_GROWTH, this::handleEyrieGrowth);
        eventManager.register(EventType.ZUBBA_ATTACK, this::handleZubbaAttack);
        eventManager.register(EventType.DAM_BREAK, this::handleDamBreak);
    }

    private void handleSeasonChange(Event event) {
        Season newSeason = (Season)event.getData();
        playSeasonTransition(newSeason);
        updateEnvironment(newSeason);
    }

    private void handleEyrieGrowth(Event event) {
        cutscenes.play("eyrie_growth");
        effects.spawn("growth_particles", event.getPosition());
        audio.playSound("growth_sound");
    }

    private void handleZubbaAttack(Event event) {
        camera.shake(0.5f, 0.2f);
        effects.spawn("zubba_swarm", event.getPosition());
        audio.playSound("zubba_buzz");
    }

    private void handleDamBreak(Event event) {
        cutscenes.play("dam_break");
        effects.spawn("water_splash", event.getPosition());
        audio.playSound("dam_crash");
    }

    public void update(float deltaTime) {
        eventManager.update(deltaTime);
        cutscenes.update(deltaTime);
        camera.update(deltaTime);
        effects.update(deltaTime);
        updateTriggers(deltaTime);
    }

    private void updateTriggers(float deltaTime) {
        for(EventTrigger trigger : triggers) {
            trigger.update(deltaTime);
        }
    }

    public void render() {
        effects.render();
        for(EventTrigger trigger : triggers) {
            trigger.render();
        }
    }

    public void dispose() {
        eventManager.dispose();
        cutscenes.dispose();
        camera.dispose();
        effects.dispose();
        audio.dispose();
        for(EventTrigger trigger : triggers) {
            trigger.dispose();
        }
        triggers.clear();
    }
}
